    private void btnloginActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnloginActionPerformed
     String username = txtuname.getText();
     String password = new String (pwdfld.getPassword());
     if (username.equals("SREERAJ")||username.equals("AASHISH"))
     {
         if(password.equals("123456"))
         {
             JOptionPane.showMessageDialog(null, "Access Granted!!");
         this.setVisible(false);
         new MAIN_FRAME().setVisible(true);
         }
         else
         {
             JOptionPane.showMessageDialog(null, "Incorrect Password!!");
             pwdfld.setText("");
         }
     }
     else
     {
         JOptionPane.showMessageDialog(null, "Access Denied!!");
         pwdfld.setText("");
         txtuname.setText("");
     }
}//GEN-LAST:event_btnloginActionPerformed

    private void btnclsActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnclsActionPerformed
         int conf = JOptionPane.showConfirmDialog(this, "Are you sure you want to exit?");
         if(conf == JOptionPane.YES_OPTION)
             System.exit(0);
         if(conf == JOptionPane.NO_OPTION)
             pwdfld.setText("");
    }//GEN-LAST:event_btnclsActionPerformed

